mapper.select:
SELECT *
FROM board
WHERE 1 = 1

    AND non = #{non}


    AND title = #{title}

mapper.insert:
INSERT INTO board
     ( NON
     , SUB_NO
     , SEQ
     , LARGE_CODE
     , MIDDLE_CODE
     , TITLE
     , CONTENTS
     , LEVEL2
     , FILE_PATH
     , FILE_NAME
     , REG_DATE
     , REG_USER
     , MOD_DATE
     , MOD_USER
     )
VALUES
     ( #{non}
     , #{subNo}
     , #{seq}
     , #{largeCode}
     , #{middleCode}
     , #{title}
     , #{contents}
     , #{level2}
     , #{filePath}
     , #{fileName}
     , #{regDate}
     , #{regUser}
     , #{modDate}
     , #{modUser}
     )
